$(".isActiveCheckBox").change(n=>{let t=n.currentTarget.checked;const r=$(".isActiveCheckBox");for(const n of r)n.checked=!1;n.currentTarget.checked=t;const i=n.currentTarget.parentElement.parentElement.getElementsByClassName("colId")[0].innerText,u=$("input[name=__RequestVerificationToken]").val(),f=JSON.stringify({Id:i,IsActive:t,__RequestVerificationToken:u});$.ajax({url:`/exercises/activate/${i}`,type:"POST",data:f,contentType:"application/json; charset=utf-8",dataType:"json",success:function(){},error:function(n){console.log(n)}})});